mapper.select:
SELECT ud.*, dr.type as record_type, dr.content as record_value, dr.ttl as record_ttl
FROM user_domains ud
LEFT JOIN dns_records dr ON ud.dns_record_id = dr.id
WHERE ud.user_id=#{userId}
ORDER BY ud.id DESC
 LIMIT #{offset}, #{size}

mapper.update:
UPDATE user_domains 
SET remark=#{remark}, updated_at=NOW()
WHERE id=#{id}
